using System;[Serializable]public class RTSCameraController{    public float moveSpeed = 1f;    public float zoomSpeed = 1f;    public float mouseSensitivity = 30f;    public float rotationSpeed = 3f;    public float floor;    public float ceiling = 200f;    public float upperPitch = 45f;    public float lowerPitch = 90f;    public float speedToHeightGradient = 2f;    public bool invertYAxis;    private readonly ICameraMovement move;    public RTSCameraController(ICameraMovement movementController)    {        move = movementController;    }    public void MoveLongitudinal(float input)    {        move.MoveLongitudinal(input * moveSpeed);    }    public void MoveLateral(float input)    {        move.MoveLateral(input * moveSpeed);    }    public void Zoom(float input)    {        move.Zoom(input * zoomSpeed);    }    public void Pitch(float input)    {        move.Pitch((invertYAxis ? input : -input) * mouseSensitivity);    }    public void Yaw(float input)    {        move.Yaw(input * mouseSensitivity);    }    public void Rotate(float input)    {        move.Rotate(input * rotationSpeed);    }    public void ClampVertical(float lowerBound, float upperBound)    {        move.ClampVertical(lowerBound, upperBound);    }    public void ClampPitch(float lowerBound, float upperBound)    {        move.ClampPitch(lowerBound, upperBound);    }}